-- ----------------------------
-- Table structure for blog_person
-- ----------------------------
DROP TABLE IF EXISTS `blog_person`;
CREATE TABLE `blog_person`  (
  `about_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `about_title` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '标题',
  `about_author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '个人姓名',
  `about_job` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '个人职业',
  `about_weixin` varchar(80) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '个人微信号',
  `about_email` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '个人电子邮箱',
  `about_pic` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '头像路径',
  `about_key` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '介绍键名',
  `about_des` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '描述',
  `about_content` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '个人介绍文章',
