) ENGINE = MyISAM AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of blog_person
-- ----------------------------
INSERT INTO `blog_person` VALUES (1, '关于我', '阿伟', '自由职业者 | PHP爱好者', '15360792817', '1294905223@qq.com', 'uploads/tou1532747864929.jpeg', '个人博客，个人介绍', '这是我的个人简介页面', '<p style=\"text-indent: 2em;\"><strong><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 黑体, SimHei; font-size: 18px;\">我叫xxx，来自美丽海边城市xx，蓝天碧海金沙滩是我家乡最引以为傲的地方。我性格比较温和、谦虚、认真细致、踏实、吃苦耐劳、有较强的责任心和社会适应能力。 　　</span></strong></p><p style=\"text-indent: 2em;\"><strong><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 黑体, SimHei; font-size: 18px;\">作为一名来自xx学院金融专业的应届毕业生的学生，我在大学四年的学习中对银行业有了比较全面的了解，同时我也感觉自己对银行的运作有较大的兴趣。 　　</span></strong></p><p style=\"text-indent: 2em;\"><strong><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 黑体, SimHei; font-size: 18px;\">我在学校中连续三次获得校奖学金，学习成绩也一直处在班级前20%的水平。相关专业课成绩同样也都在80以上。 在校时我还担任班里的副班长，配合班长共同管理班级，组织班级活动。 　　</span></strong></p><p style=\"text-indent: 2em;\"><strong><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 黑体, SimHei; font-size: 18px;\">虽然我只是应届生，并没有什么实际工作经验，但我有很好的学习能力和饱满的工作热情。我想贵行所需要的也正是 像我这样对工作充满热情的人。 　　</span></strong></p><p style=\"text-indent: 2em;\"><strong><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-family: 黑体, SimHei; font-size: 18px;\">我认为我有能力也有信心做好这份工作，希望大家能够认可我，给我这个机会。</span></strong></p>');

-- ----------------------------
-- Table structure for blog_slide
-- ----------------------------
DROP TABLE IF EXISTS `blog_slide`;
CREATE TABLE `blog_slide`  (
  `slide_id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '轮播图主键',
  `slide_name` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '轮播图名称',
  `slide_url` varchar(250) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '轮播图地址',
  `slide_pic` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '轮播图片路径',
  `slide_sort` int(11) DEFAULT 0 COMMENT '轮播图排序',
  PRIMARY KEY (`slide_id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '轮播图表' ROW_FORMAT = Dynamic;

SET FOREIGN_KEY_CHECKS = 1;
